
var types = [
	"credentials",
	"server",
	"application",
	"plugins",
	"routes",
	"style"
];

module.exports = function(type) {
	if(types.indexOf(type) != -1) {
		if(type == 'credentials' || type == 'server') {
			if(typeof window != 'undefined') {
				return null;
			} else {
				var sources = {
					credentials: require('./../../../configs/credentials'+'.json'),
					server: require('./../../../configs/server'+'.json')
				};
				return sources[type];
			}
		} else {
			var sources = {
				application: require('./../../../configs/application.json'),
				plugins: require('./../../../configs/plugins.json'),
				routes: require('./../../../configs/routes.json'),
				style: require('./../../../configs/style.json')
			};
			return sources[type];
		}
	} else {
		return null;
	}
}
