
const routes = require('./../../../configs/routes.json');

var Router = require('./router.js');

var router = new Router;

routes.forEach(function(e) {
	var path = e.path;
	var status = e.status;
	var title = e.title;
	var name = e.name;
	var handler = require('./../../../application/routes/react/'+name+'/handler.jsx');
	var express = require('./../../../application/routes/react/'+name+'/server.js');
	router.add(path, handler, status, title, express);
});


module.exports = router;
