var reactRoute = require('reactRoute');

var router = new Router;

var ___ = function() {
	require('./../../../application/routes/**/handler.jsx', { glob: true });
};



routes.pages.forEach(function(e) {
	var {path, status, title, name} = e;
	var handler = reactRoute(require('./../../../application/routes/'+name+'/react/handler.jsx'));
	var express = null;
	router.add(path, handler, status, title, express);
});




module.exports = router;
