"use strict";


var side = function(a, b, c) {
	var serverSide = typeof window == 'undefined';
	var server = function() {};
	var client = function() {};
	var ret = function() {};
	var run = false;
	if(typeof a == 'function') {
		server = a || server;
		client = b || client;
		run = typeof c == 'undefined' ? run : c;
		ret = serverSide ? server : client;
	} else if(typeof a == 'object') {
		server = a.server || server;
		client = a.client || client;
		run = typeof b == 'undefined' ? run : b;
		ret = serverSide ? server : client;
	}
	if(run) {
		return ret();
	} else {
		return ret;
	}
};

module.exports = side;
